catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

	// alert('newValue = ' + newValue);

    var ga = new GlideAjax('x_g_sonc_ns_pro.cmdb_ci_site_ajax');
    ga.addParam('sysparm_name', 'getServices');
    ga.addParam('sysparm_site_id', newValue);
    ga.getXMLAnswer(getSvcs);

    var ga2 = new GlideAjax('x_g_sonc_ns_pro.cmdb_ci_site_ajax');
    ga2.addParam('sysparm_name', 'getBilling');
    ga2.addParam('sysparm_site_id', newValue);
    ga2.getXMLAnswer(getBills);

    function getSvcs(response) {

		//alert('getSvcs response = ' + response);

        var svc_response = JSON.parse(response);
		var svcs = '';
		for (i=0; i < svc_response.length; i++) {
			if (svc_response[i].end_date == "") {

				// alert('adding service ' + svc_response[i].svc_title);

				svcs = svcs + svc_response[i].svc_title + '\n';
			}
		}
        g_form.setValue('current_services', svcs);

    }

    function getBills(response) {

		alert('getBills response = ' + response);

        var bills_response = JSON.parse(response);
		var bills = '';
		for (i=0; i < bills_response.length; i++) {

			alert('adding bill ' + bills_response[i].billing_code);

			bills = bills + bills_response[i].billing_code + '\n';
		}
        g_form.setValue('current_billing', bills);

    }

}
