catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

	// alert('oldValue = ' + oldValue + '\nnewValue = ' + newValue);
	var service_level;
	if (newValue == 'true') {
		// if HA then service category is always 'Guaranteed'
		g_form.setValue('network_service_category', 'guaranteed');
		g_form.setReadOnly('network_service_category', true);
		service_level = 'gold';
	}
	else {
		g_form.setReadOnly('network_service_category', false);
		service_level = 'silver';
		if (g_form.getValue('network_service_level') == 'best_effort') {
			service_level = 'bronze';
		}
	}

	// alert('service level = ' + service_level);

	var ga = new GlideAjax('x_g_sonc_ns_pro.service_definition_ajax');
    ga.addParam('sysparm_name', 'getSvcByTypeAndLevel');
    ga.addParam('sysparm_svc_type', 'wan');
	ga.addParam('sysparm_svc_level', service_level);
    ga.getXMLAnswer(getSvcs);

    function getSvcs(response) {

		// alert(response);

        var svcs = JSON.parse(response);
		g_form.clearOptions('service_code');
		g_form.addOption('service_code', '', '-- None --');
		for (var i = 0; i < svcs.length; i++) {
			g_form.addOption('service_code', svcs[i].code, svcs[i].title);
		}
    }	

}
