catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    var ga = new GlideAjax('x_g_sonc_ns_pro.cmdb_ci_site_ajax');
    ga.addParam('sysparm_name', 'getSite');
    ga.addParam('sysparm_site_sys_id', newValue);
    ga.getXMLAnswer(getSite);

    function getSite(response) {

        var site = JSON.parse(response);
        g_form.setValue('county', site.county);
        g_form.setValue('city', site.city);
        g_form.setValue('street', site.street);
        g_form.setValue('zip_code', site.zip);
        g_form.setValue('primary_site_contact', site.primary_poc);
        g_form.setValue('alternate_site_contact', site.alternate_poc);

    }

}
