catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

	var startingIP = g_form.getValue('starting_ip_address');
	var endingIP = newValue;
	//alert("starting ip: " + startingIP);
	//alert("ending ip: " + endingIP);
	var ga = new GlideAjax('x_g_sonc_ns_pro.core_nspro_ajax');
	ga.addParam('sysparm_name','validateIPInput');
	ga.addParam('sysparm_startingIP',startingIP);
	ga.addParam('sysparm_endingIP',endingIP);
	ga.getXMLAnswer(checkRange);

	function checkRange(response){
		var answer = JSON.parse(response);
		if(answer.validate != "valid"){
			alert(answer.validate);
		} else {
			g_form.setValue('numberOfIPsInRange',answer.totalIPs);
			g_form.setValue('numberOfVLansInRange',answer.totalVlans);
		}
	}


}
