catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading) {
        return;
    }

    if (newValue == '') {
        g_form.setMandatory('justification', false);
        g_form.setDisplay('justification', false);
        return;
    }
    //alert('newValue = ' + newValue);

    var ga = new GlideAjax('x_g_sonc_ns_pro.core_nspro_ajax');
    ga.addParam('sysparm_name', 'requiresJustification');
    ga.addParam('sysparm_new_date', newValue);
    ga.addParam('sysparm_created_on', g_form.getValue('created_on'));
    ga.addParam('sysparm_request_type', g_form.getValue('type_request'));
    ga.addParam('sysparm_change_type', g_form.getValue('type_change'));
    ga.getXMLAnswer(checkDate);

    function checkDate(response) {

        //alert('response = ' + response);

        if (response == 'true') {
            g_form.setDisplay('justification', true);
            g_form.setMandatory('justification', true);
        } else {
            g_form.setMandatory('justification', false);
            g_form.setDisplay('justification', false);
        }


    }

}
