catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
if (isLoading || newValue == '') {
	return;
}

	// alert('onChange running with ' + newValue);

	switch (newValue) {
	case "netplus_type_service":
		var ga = new GlideAjax('x_g_sonc_ns_pro.billing_ajax');
		ga.addParam('sysparm_name', 'getNetplusCodes');
		ga.getXMLAnswer(getCodes);
		break;
	case "netplus_cac":
		var ga2 = new GlideAjax('x_g_sonc_ns_pro.billing_ajax');
		ga2.addParam('sysparm_name', 'getNetplusCircuitCodes');
		ga2.getXMLAnswer(getCodes);
		break;
	case "netplus_cpe_level":
		var ga3 = new GlideAjax('x_g_sonc_ns_pro.billing_ajax');
		ga3.addParam('sysparm_name', 'getCPEModelCodes');
		ga3.getXMLAnswer(getCPECodes);
		break;				
	}

	function getCodes(response) {

		// alert('getCodes response = ' + response);

		var code_response = JSON.parse(response);
		var codes = '';
		g_form.clearOptions('billing_code');
		for (i=0; i < code_response.length; i++) {
			// alert('adding code ' + code_response[i].description);
			g_form.addOption('billing_code', code_response[i].item_code, code_response[i].description);
		}

	}

	function getCPECodes(response) {

		// alert('getCPECodes response = ' + response);

		var code_response = JSON.parse(response);
		var desc = '';
		g_form.clearOptions('billing_code');
		for (i=0; i < code_response.length; i++) {

			// alert(code_response[i].cpe_model_1 + '\n' + code_response[i].cpe_model_2 + '\n' + code_response[i].cpe_model_3 + '\n' + code_response[i].cpe_model_4);

			desc = code_response[i].cpe_model_1;
			// only add the other codes if they're not blank	
			if (code_response[i].cpe_model_2) desc+= "/" + code_response[i].cpe_model_2;
			if (code_response[i].cpe_model_3) desc+= "/" + code_response[i].cpe_model_3;
			if (code_response[i].cpe_model_4) desc+= "/" + code_response[i].cpe_model_4;

			desc+=  " : Port Rg Lo-" + code_response[i].port_range_low + " : Port Rg Hi-" + code_response[i].port_range_high;

			// alert('Final desc = ' + desc);

			g_form.addOption('billing_code', code_response[i].cpe_level + '[' + i + ']', desc);

		}
	}

}
