catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

    var ga = new GlideAjax('x_g_sonc_ns_pro.cmdb_ci_site_ajax');
    ga.addParam('sysparm_name', 'getServices');
    ga.addParam('sysparm_site_id', newValue);
    ga.getXMLAnswer(getSvcs);

    function getSvcs(response) {

		// alert('getSvcs response = ' + response);

        var svc_response = JSON.parse(response);
		var svcs = '';
		g_form.clearOptions('terminate_service');
		for (i=0; i < svc_response.length; i++) {
			if (svc_response[i].end_date == "") {
				// add to list for existing services
				svcs = svcs + svc_response[i].svc_title + '\n';
				// add to dropdown select box
				g_form.addOption('terminate_service', svc_response[i].sys_id, svc_response[i].svc_title);
			}
		}
        g_form.setValue('existing_services', svcs);

    }

}
