catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
    var ga = new GlideAjax('x_g_sonc_ns_pro.cmdb_ci_site_ajax');
    ga.addParam('sysparm_name', 'getSite');
    ga.addParam('sysparm_sys_id', newValue);
    ga.getXMLAnswer(getSite);

    function getSite(response) {

		// alert('response = ' + response);

		var site_info = "";
        var site = JSON.parse(response);
		site_info += site.street + "\n";
		site_info += site.city + "\n";
		site_info += site.state + "\n";
		site_info += site.zip + "\n";
		site_info += site.county + " County";
		g_form.setValue('current_site_info', site_info);

		// set POCs now to avoid an additional script/callback function
		g_form.setValue('current_primary_poc', site.primary_poc);
		g_form.setValue('current_alternate_poc', site.alternate_poc);

    }

}
