{"host_circuit_valid_change":{"scriptActive":true,"script":"var changeArray = fd_data.trigger.changed_fields;
var outputObj;
var isValidChange = false;
for(var i = 0;i < changeArray.length;i++){
 if(changeArray[i].field_name == "circuit"){
 outputObj = changeArray[i];
 if(outputObj.previous_value){
 isValidChange = true;
 }
 break;

 }
}
return isValidChange;"}}

