sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var gr = new GlideRecord('x_g_sonc_ns_pro_billing_changes');
	gr.addQuery('action', 'add');
	gr.addQuery('billing_type', 'netplus_type_service');
	gr.addQuery('state', 'new');
	gr.setLimit(1);
	gr.query();
	if (gr.next()) {
		// gs.warn('found record, need to update row');
		gr.billing_type = 'netplus_type_service';
		gr.billing_code = current.getDisplayValue('netplus_type_service');
		// gr.network_request = current.getValue('telco_order');
		gr.service = current.getUniqueValue();
		gr.site = current.getValue('site');
		gr.update();		
	}
	else {
		// need to add row
		// gs.warn('no existing record, need to add row');
		gr.initialize();
		gr.action = 'add';
		gr.state = 'new';
		gr.billing_type = 'netplus_type_service';
		gr.billing_code = current.getDisplayValue('netplus_type_service');
		// gr.network_request = current.getValue('telco_order');
		gr.service = current.getUniqueValue();
		gr.site = current.getValue('site');
		gr.insert();
	}

})(current, previous);
