sys_script_client.script:
function onLoad() {
    if (!g_scratchpad.taskInformation) {
        if (g_user.getClientData('json')) {
            //Take the string and convert it back to an object
            var taskInformation = JSON.parse(g_user.getClientData('json'));
            g_scratchpad.taskInformation = taskInformation;
            var clearSession = new GlideAjax('global.setSession');
            clearSession.addParam('sysparm_name', 'clearSessionVar');
            clearSession.getXML(

                function(response) {
                    //no need for response
                    return;
                });



        }
    } 
}
