sys_script_client.script:
function onLoad() {
    var siteSysID = g_form.getValue('x_g_sonc_ns_pro_site');
	var fieldName = 'x_g_sonc_ns_pro_site';

    if (siteSysID) {
        var newSite_ga = new GlideAjax('x_g_sonc_ns_pro.fieldStyleHelper_ajax');
        newSite_ga.addParam('sysparm_name', 'isNewSite');
        newSite_ga.addParam('sysparm_siteSysID', siteSysID);
		newSite_ga.getXML(_getAnswer);
    }

	function _getAnswer(response){
		var answer = response.responseXML.documentElement.getAttribute("answer");
		answer = JSON.parse(answer);
		if(answer.status == 'success'){
			if(answer.message == true){
				var site = g_form.getDisplayBox(fieldName);
				site.style.background = 'PaleGreen';
				var siteLabel = g_form.getLabel(fieldName);
				siteLabel.style.color = 'green';
				siteLabel.style.fontWeight = 'bold';	
			}
		}

	}

}
