sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var ga = new GlideAjax('x_g_sonc_ns_pro.cmdb_ci_site_ajax');
    ga.addParam('sysparm_name', 'getLocation');
    ga.addParam('sysparm_site_sys_id', newValue);
    ga.getXMLAnswer(getLoc);

}

function getLoc(response) {

    alert(response);
    var loc = JSON.parse(response);
    g_form.setValue('location', loc.sysId);

    // var request_type = g_form.getValue('request_type');
    // alert('reqtype = ' + request_type);

    //if (request_type != 'b4cd7d941bb57d90824752c1604bcbd4') { // if not relocate request type
        // set destination fields
        g_form.setValue('dest_site', g_form.getValue('site'));
        g_form.setValue('dest_location', loc.sysId);
        g_form.setValue('dest_county', loc.county);
        g_form.setValue('dest_street', loc.street);
        g_form.setValue('dest_city', loc.city);
        g_form.setValue('dest_state', loc.state);
        g_form.setValue('dest_zip', loc.zip);
    //}

}
