sys_script_client.script:
function onLoad() {

	// set mandatory fields
	var type_req = g_form.getValue('type_request');

	//alert('type_req = ' + type_req);

	if (type_req == 'New Service') {
		// set mandatory fields for new service request
		g_form.setMandatory('wan_cpe_ports_required', true);
		g_form.setMandatory('wan_circuit_type_jack', true);
		g_form.setMandatory('assigned_wan_service', true);
		g_form.setMandatory('network_service_category', true);
		g_form.setMandatory('network_service_subcategory', true);
		g_form.setMandatory('required_wan_cpe_count', true);
	}

   var svc_def = g_form.getValue('assigned_wan_service');

    var ga = new GlideAjax('x_g_sonc_ns_pro.service_definition_ajax');
    ga.addParam('sysparm_name', 'getSvcDef');
    ga.addParam('sysparm_sysid', svc_def.toString());
    ga.getXMLAnswer(getSvcDef);

    function getSvcDef(response) {

		// alert(response);

        var def = JSON.parse(response);

		g_form.setValue('telco_order.network_service_category', 'enterprise');

		g_form.setValue('quoted_cost', def.cost_per_month_core);
		g_form.setValue('quoted_service_definition', svc_def.toString());
        g_form.setValue('assigned_wan_service_code', def.svc_code);
		g_form.setValue('quoted_service_code', def.svc_code);
		g_form.setValue('requested_service_code', def.svc_code);
		g_form.setValue('service_level', def.svc_ha_category);
		g_form.setValue('requested_speed', def.speed_ingress);
		g_form.setValue('circuit_speed_ingress', def.speed_ingress);
		g_form.setValue('circuit_speed_egress', def.speed_egress);

		// alert('service_level = ' + g_form.getValue('service_level'));

    }

}
