sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading ) {
        return;
    }
	//alert('new ' + newValue);
	//alert('old ' + oldValue);
    if (oldValue != newValue || (oldValue == '' && newValue == '')) {
		//alert(newValue);
        if (newValue == ''||!newValue) {
            g_form.setValue('serial_number', '');
        } else {
            var netGearRef = g_form.getReference('network_gear', populateSerialCallback);
        }




    }

    function populateSerialCallback(netGearRef) {
        var serialNumber = netGearRef.serial_number;
        g_form.setValue('serial_number', serialNumber);

    }
}
