sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading) {
        return;
    }

    if (oldValue != newValue) {
		g_form.setValue('assigned_pop','');
		g_form.setValue('assigned_pe','');
		g_form.setValue('assigned_pe_port','');
		g_form.setValue('circuit','');

        var taskInformation = g_scratchpad.taskInformation;
        var taskTelcoChange = new GlideAjax('x_g_sonc_ns_pro.nspro_task_ajax');
        taskTelcoChange.addParam('sysparm_name', 'taskTelcoProviderChanged');
        taskTelcoChange.addParam('sysparm_taskInformation', JSON.stringify(taskInformation));
        taskTelcoChange.getXMLAnswer(getResponse);


    }

    function getResponse(response) {
		//alert(response);
        var responseObj = JSON.parse(response);
		//alert(responseObj.resultCode);
        if (responseObj.resultCode != 'success') {
            g_form.addErrorMessage(response.message);
			alert(response.message);
        }

        GlideList2.get(g_form.getTableName() + '.REL:99067d2487455a90f9f20f280cbb35dd').setFilterAndRefresh('');
        GlideList2.get(g_form.getTableName() + '.REL:8f576281c3100200e280dccdf3d3aed8').setFilterAndRefresh('');
        GlideList2.get(g_form.getTableName() + '.REL:49759ddd470192d040fd43ff336d4392').setFilterAndRefresh('');
        GlideList2.get(g_form.getTableName() + '.REL:f677d2e247895a1040fd43ff336d4391').setFilterAndRefresh('');
        GlideList2.get(g_form.getTableName() + '.REL:a217747e4709da1040fd43ff336d4303').setFilterAndRefresh('');
		location.reload();


    }

}
