sys_script_fix.script:
var gr = new GlideRecord('x_g_sonc_dit_nsw22_nsw2_vrf_definitions');
gr.query();


while (gr.next()) {
    var newVRF = new GlideRecord('x_g_sonc_ns_pro_vrf_definition');
    newVRF.initialize();
    newVRF.setValue('vrf_name', gr.getValue('vrf_name'));
    newVRF.setValue('vrf_number', gr.getValue('vrf_number'));
    newVRF.setValue('vrf_prefix', gr.getValue('vrf_prefix'));
    newVRF.setValue('vrf_usage', gr.getValue('vrf_usage'));
    newVRF.setValue('short_description', gr.getValue('short_description'));
    newVRF.insert();

}
