sys_script_include.script:
var service_definition_ajax = Class.create();
service_definition_ajax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getSvcDef: function() {

        var svc_def = new x_g_sonc_ns_pro.service_definition();
        var sysid = this.getParameter('sysparm_sysid');
        return svc_def.getSvcDef(sysid);

    },

	getSvcByTypeAndLevel: function() {

		var svc_def = new x_g_sonc_ns_pro.service_definition();
		var svc_type = this.getParameter('sysparm_svc_type');
        var svc_level = this.getParameter('sysparm_svc_level');
        return svc_def.getSvcByTypeAndLevel(svc_type, svc_level);
	},

    type: 'service_definition_ajax'
});
