sys_script_include.script:
var core_nspro_modals = Class.create();
core_nspro_modals.prototype = {
    initialize: function() {
    },
	multiSelectContent: function(contentObj){
		var errorString;
		var content;
		try {
			if(!contentObj){
				throw new IllegalArgumentException("Parameter contentObj cannot be empty or null");
			}
			var id = contentObj.id;
			var title = contentObj.title;
			var options = contentObj.options;

			content = NSPROConstants.MODALS.MULTISELECT_HEADER;
			for (option in options){
				var selectOption = options[option];
				var optionContent = '<option value="' + selectOption.optionValue + '" role="option">' + selectOption.optionText + '</option>';
				content = content + optionContent;
			}
			content = content + NSPROConstants.MODALS.MULTISELECT_FOOTER;

			content = content.replaceAll('|ID|',id);
			content = content.replaceAll('|TITLE|',title);
		} catch(Ex){
            errorString = "Error Occured in core_nspro_modals.multiSelectContent - " + Ex.toString();
            gs.info(errorString);
		}
        if (errorString) {
            return errorString;
        } else {
            return content;
        }
	},

    type: 'core_nspro_modals'
};
