sys_script_include.script:
var sys_user = Class.create();
sys_user.prototype = {
    initialize: function() {},

    getUser: function(userId) {

        gs.info('sys_user.getUser searching for user ' + userId);

        var user = {};
        var gr = new GlideRecord('sys_user');
        if (gr.get(userId)) {

            // gs.info('sys_user.getUser found user ' + gr.name);

			user.active = gr.active;
            user.name = gr.name.toString();
            user.phone = gr.phone.toString();
            user.email = gr.email.toString();
            user.manager = gr.manager.toString();
            user.state = gr.state.toString();
            user.zip = gr.zip.toString();
            user.street = gr.street.toString();
            user.state = gr.state.toString();
			user.city = gr.city.toString();
			user.agency = gr.u_agency.toString();
			user.department = gr.department.toString();
			user.last_login = gr.last_login.toString();
			user.vip = gr.vip;
        } else {
            gs.error('user not found');
        }
        return global.JSON.stringify(user);
    },

    type: 'sys_user'
};
