sys_script_include.script:
var sys_user = Class.create();
sys_user.prototype = {
    initialize: function() {},

    getUser: function(userId) {

        gs.info('sys_user.getUser searching for user ' + userId);

        var user = {};
        var gr = new GlideRecord('sys_user');
        if (gr.get(userId)) {

            gs.info('sys_user.getUser found user ' + gr.name);

			user.active = gr.getValue('active');
            user.name = gr.getValue('name');
            user.phone = gr.getValue('phone');
            user.email = gr.getValue('email');
            user.manager = gr.getValue('manager');
            user.state = gr.getValue('state');
            user.zip = gr.getValue('zip');
            user.street = gr.getValue('street');
            user.state = gr.getValue('state');
			user.city = gr.getValue('city');
			user.agency = gr.getValue('u_agency');
			user.department = gr.getValue('department');
			user.last_login = gr.getValue('last_login');
			user.vip = gr.getValue('vip');
        } else {
            gs.error('user not found in SI sys_user.getUser');
        }
        return global.JSON.stringify(user);
    },

    type: 'sys_user'
};
