sys_script_include.script:
var sys_user_ajax = Class.create();
sys_user_ajax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getUser: function() {

		gs.info('sys_user_ajax.getUser running...');

        var user = new x_g_sonc_ns_pro.sys_user();
        var user_id = this.getParameter('sysparm_user_id');

		gs.info('sys_user_ajax.getUser running for user ' + user_id);

        return user.getUser(user_id);

    },

    type: 'sys_user_ajax'
});
