sys_script_include.script:
function nspro_getSvcs(ha) {

	// https://servicenowguru.com/scripting/script-includes-scripting/advanced-reference-qualifier-script-include/

	// gs.info('nspro_getSvcs has been called with ha = ' + ha);
	var svcs = [];
	var gr = new GlideRecord('x_g_sonc_ns_pro_service_definition');
	gr.addQuery('svc_title','CONTAINS', 'WAN');
	if ( ha == 'true') {
		// gs.info('adding query for Gold services');
		gr.addQuery('svc_ha_category', 'Gold');
	}
	else {
		gr.addQuery('svc_ha_category', '!=', 'Gold');
	}
	gr.orderBy('speed_egress');

	// gs.info('query = ' + gr.getEncodedQuery());

	gr.query();
	// gs.info('Query returned ' + gr.getRowCount() + ' rows');
	while (gr.next()) {
		svcs.push(gr.getUniqueValue());
	}

	// gs.info ('svcs = ' + svcs.join(','));

	return svcs;

}
