sys_script_include.script:
var fieldStyleHelper_ajax = Class.create();
fieldStyleHelper_ajax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    isNewSite: function() {
        var answer = {};
        var errorString;
        try {
            var siteSysID = this.getParameter('sysparm_siteSysID');
            var fieldHelper = new global.fieldStyleHelper();
            var result = fieldHelper.isNewSite(siteSysID);

        } catch (ex) {
            errorString = "Error Occured in fieldStyleHelper_ajax - " + ex.toString();
        }
		if(errorString){
			answer.status = NSPROConstants.AJAX_RETURNCODES.FAILED;
			answer.message = errorString;
		} else{
			answer.status = NSPROConstants.AJAX_RETURNCODES.SUCCESS;
			answer.message = result;
		}
		return JSON.stringify(answer);
    },
    type: 'fieldStyleHelper_ajax'
});
