sys_script_include.script:
var request_type = Class.create();
request_type.prototype = {
    initialize: function() {},

    getType: function(type) {

        gs.info('request_type.getType searching for type ' + type);

        var gr = new GlideRecord('x_g_sonc_ns_pro_request_type');
        if (gr.get(type)) {

            gs.info('request_type.getType returning ' + gr.type.toString().toLowerCase());

            return gr.type.toString().toLowerCase();
        }
		else {
			gs.error('Could not locate type for ' + type);
		}
        return;

    },

    type: 'request_type'
};
