sys_script_include.script:
var circuit_ajax = Class.create();
circuit_ajax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getHostCircuitInformation: function(){
		var hostCircuitSysID = this.getParameter('sysparm_rowSysID');
		var hostCircuit = new x_g_sonc_ns_pro.circuit();
		response = hostCircuit.getHostCircuitInformation(hostCircuitSysID);
		//gs.info('hostcircuit response ' + JSON.stringify(response));
		if(response.message.startsWith('Error Occured ')){
			response.resultCode = NSPROConstants.AJAX_RETURNCODES.FAILED;
		}else {
			response.resultCode = NSPROConstants.AJAX_RETURNCODES.SUCCESS;
		}
		return JSON.stringify(response);

	},
	createTemporaryCircuit: function(){
		var taskSysID = this.getParameter('sysparm_taskSysID');
		var localCircuit = new x_g_sonc_ns_pro.circuit();
		response = localCircuit.createTemporaryCircuit(taskSysID);

		if(response.message.startsWith('Error Occured ')){
			response.resultCode = NSPROConstants.AJAX_RETURNCODES.FAILED;
		}else {
			response.resultCode = NSPROConstants.AJAX_RETURNCODES.SUCCESS;
		}
		return JSON.stringify(response);

	},
    type: 'circuit_ajax'
});
