sys_script_include.script:
var core_nspro_modals_ajax = Class.create();
core_nspro_modals_ajax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	constructMultiSelectContent: function(){
		var answer = {};
		var nsProModals = new core_nspro_modals();
		contentObjString = this.getParameter('sysparm_contentObjString');
		contentObj = JSON.parse(contentObjString);

		content = nsProModals.multiSelectContent(contentObj);

		if(content.startsWith("Error")){
			answer.status = NSPROConstants.AJAX_RETURNCODES.FAILED;
			answer.message = content;
		} else {
			answer.status = NSPROConstants.AJAX_RETURNCODES.SUCCESS;
			answer.message = content;
		}
		return JSON.stringify(answer);
	},
    type: 'core_nspro_modals_ajax'
});
