sys_script_include.script:
var telco_ajax = Class.create();
telco_ajax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getTelcoFromZipSelection: function(){
		var rowSysID = this.getParameter('sysparm_rowSysID');
		var telco = new x_g_sonc_ns_pro.telco();
		response = telco.getTelcoByZipSelection(rowSysID);
		if(response.message.startsWith('Error Occured ')){
			response.resultCode = NSPROConstants.AJAX_RETURNCODES.FAILED;
		}else {
			response.resultCode = NSPROConstants.AJAX_RETURNCODES.SUCCESS;
		}
		return JSON.stringify(response);
	},
	getSelectedTelcoInformation: function(){
		var rowSysID = this.getParameter('sysparm_rowSysID');
		var telco = new x_g_sonc_ns_pro.telco();
		response = telco.getSelectedTelcoInformation(rowSysID);
		if(response.message.startsWith('Error Occured ')){
			response.resultCode = NSPROConstants.AJAX_RETURNCODES.FAILED;
		}else {
			response.resultCode = NSPROConstants.AJAX_RETURNCODES.SUCCESS;
		}
		return JSON.stringify(response);
	},
    type: 'telco_ajax'
});
