sys_script_include.script:
var port_ajax = Class.create();
port_ajax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    canBeAssigned: function() {
        var portSysID = this.getParameter('sysparm_rowSysID');
        port = new x_g_sonc_ns_pro.port();
        response = port.canBeAssigned(portSysID);
        if (response.message.startsWith('Error Occured ')) {
            response.resultCode = NSPROConstants.AJAX_RETURNCODES.FAILED;
        } else {
            response.resultCode = NSPROConstants.AJAX_RETURNCODES.SUCCESS;
        }
        return JSON.stringify(response);

    },
	removePortsFromLocalCircuit: function(){
		var localCircuitSysID = this.getParameter('sysparm_rowSysID');
		var checkedPorts = this.getParameter('sysparm_checkedPorts');

		var port = new x_g_sonc_ns_pro.port();
		var response = port.removePortsFromLocalCircuit(localCircuitSysID,checkedPorts);

        if (response.message.startsWith('Error Occured ')) {
            response.resultCode = NSPROConstants.AJAX_RETURNCODES.FAILED;
        } else {
            response.resultCode = NSPROConstants.AJAX_RETURNCODES.SUCCESS;
        }
        return JSON.stringify(response);

	},
	removePortsFromSite: function(){
		var siteSysID = this.getParameter('sysparm_rowSysID');
		var checkedPorts = this.getParameter('sysparm_checkedPorts');

		var port = new x_g_sonc_ns_pro.port();
		var response = port.removePortsFromSite(siteSysID,checkedPorts);

        if (response.message.startsWith('Error Occured ')) {
            response.resultCode = NSPROConstants.AJAX_RETURNCODES.FAILED;
        } else {
            response.resultCode = NSPROConstants.AJAX_RETURNCODES.SUCCESS;
        }
        return JSON.stringify(response);

	},
    type: 'port_ajax'
});
