sys_script_include.script:
var service_code = Class.create();
service_code.prototype = {
    initialize: function() {
    },

	////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
	getSvcDef: function(sysId) {
	////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

		var gr = new GlideRecord('x_g_sonc_ns_pro_service_code');
		svcDef = {};
		if (gr.get(sysId)) {
			if (this.debug) gs.info(this.timestamp() + ' Found service_code definition ' + gr.svc_title.getDisplayValue());

			svcDef.sys_id = gr.getUniqueValue();
			svcDef.svc_type = gr.getValue('svc_type');
			svcDef.svc_title = gr.getValue('svc_title');
			svcDef.svc_code = gr.getValue('svc_code');
			svcDef.speed_ingress = gr.getValue('speed_ingress');
			svcDef.speed_egress = gr.getValue('speed_egress');
			svcDef.cost_per_month_transport = gr.getValue('cost_per_month_transport');
			svcDef.cost_per_month_core = gr.getValue('cost_per_month_core');
			svcDef.netplus = gr.getValue('netplus');
		}
		else {
			gs.info('failed to find service_code definition');
		}
		return global.JSON.stringify(svcDef);
	},	

    type: 'service_code'
};
