sys_script_include.script:
var core_nspro = Class.create();
core_nspro.prototype = {
    initialize: function() {},

    getDaysFromToday: function(days) {

        var gd = new GlideDate();
        gd.addDaysLocalTime(days);
        return gd.getDisplayValue();

    },

    getDaysFromDate: function(date, days) {

        var gd = new GlideDate();
        gd.setValue(date);
        gd.addDaysLocalTime(days);

        // gs.info('getDaysFromDate returning ' + gd);

        return gd;

    },

    requiresJustification: function(need_by_date, created_on, request_type, change_type) {

      //  gs.info('core_nspro().requiresJustification() \nneed_by_date = ' + need_by_date + '\ncreated_on = ' + created_on + '\nrequest_type = ' + request_type + '\nchange_type = ' + change_type);

        var lead_time = 120; // default to 120 days for new services, 45 for changes
        if (request_type == 'change') {
            lead_time = 45;

            if (change_type == 'add_ha' || change_type == 'wan_service_change' || change_type == 'wan_routing_change') {
                lead_time = 120;
            }

        }
        let grace_period = -15; // grace period for requests allowed on all request types

        var sched_date = new GlideDate();

        var temp = created_on.split('/');
        //gs.info('temp = ' + temp[2] + '-' + temp[0] + '-' + temp[1]);
        sched_date.setValue(temp[2] + '-' + temp[0] + '-' + temp[1]);
        sched_date.addDaysLocalTime(lead_time);
        sched_date.addDaysLocalTime(grace_period);
        //gs.info('core_nspro().requiresJustification() \nsched_date = ' + sched_date);

        var created_date = new GlideDate();
        temp = created_on.split('/');
        //gs.info('temp = ' + temp[2] + '-' + temp[0] + '-' + temp[1]);
        created_date.setValue(temp[2] + '-' + temp[0] + '-' + temp[1]);
        //gs.info('core_nspro().requiresJustification() \ncreated_date = ' + created_date);

        var needed_date = new GlideDate();
        temp = need_by_date.split('/');
        //gs.info('need_by_date = ' + temp[2] + '-' + temp[0] + '-' + temp[1]);
        needed_date.setValue(temp[2] + '-' + temp[0] + '-' + temp[1]);

        //gs.info('core_nspro().requiresJustification() \nneeded_date = ' + needed_date + '\nsched_date = ' + sched_date + '\nrequest_type = ' + request_type);

        if (needed_date.getValue() < sched_date.getValue()) {
            //gs.info('core_nspro().requiresJustification() returning true');
            return true;
        } else {
            //gs.info('core_nspro().requiresJustification() returning false');
            return false;
        }
    },
	requestHasProvisioningTasks(requestSysID){
		hasProvisioning = false;
		var nsTasks = new GlideRecord('x_g_sonc_ns_pro_nspro_tasks');
		nsTasks.addQuery('telco_order',requestSysID);
		nsTasks.addQuery('task_type','provisioning');
		nsTasks.query();
		if(nsTasks.next()){
			hasProvisioning = true;
		}
		return hasProvisioning;
	},

    type: 'core_nspro'
};
