sys_script_include.script:
function nspro_getSites(user_id) {

	// https://servicenowguru.com/scripting/script-includes-scripting/advanced-reference-qualifier-script-include/

	var user = new x_g_sonc_ns_pro.sys_user();
	var jsonUser = user.getUser(user_id);
	var objUser = JSON.parse(jsonUser);
	var agency = objUser.agency;

	var sites = [];
	var gr = new GlideRecord('cmdb_ci_site');
	gr.addActiveQuery();
	// if user has role nspro.admin then they can see all sites, otherwise restrict to user agency
	if (!gs.hasRole("x_g_sonc_ns_pro.admin")) {
		gr.addQuery('x_g_sonc_ns_pro_agency', agency);
	}
	//gr.setLimit(5);  // for testing
	gr.query();
	while (gr.next()) {
		sites.push(gr.getUniqueValue());
	}

	gs.info ('sites = ' + sites.join(','));

	return sites;
}
