sys_script_include.script:
var nspro_task_ajax = Class.create();
nspro_task_ajax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	taskTelcoProviderChanged: function(){
		var taskInformation = this.getParameter('sysparm_taskInformation');
		taskInformation = JSON.parse(taskInformation);
		var nsProTask = new x_g_sonc_ns_pro.nspro_task();
		var response = nsProTask.taskTelcoProviderChanged(taskInformation);

        if (response.message.startsWith('Error Occured ')) {
            response.resultCode = NSPROConstants.AJAX_RETURNCODES.FAILED;
        } else {
            response.resultCode = NSPROConstants.AJAX_RETURNCODES.SUCCESS;
        }
        return JSON.stringify(response);

	},
    type: 'nspro_task_ajax'
});
