sys_ui_action.script:
function confirmUnAssignPortsFromSite() {
	var localCircuitInformation = {};
    var localCircuitName = g_form.getValue('name');
    var formTitle = "Un-Assign Port From Local Circuit - " + localCircuitName;
  //var site = g_form.getValue('x_g_sonc_ns_pro_site');
    var localCircuitID = g_form.getUniqueValue();
	localCircuitInformation.localCircuitName = localCircuitName;
	localCircuitInformation.localCircuitID = localCircuitID;
	localCircuitInformation.checkedPorts = g_list.getChecked();
	g_scratchpad.localCircuitInformation = localCircuitInformation;

    var gm = new GlideModal("glide_confirm_standard", true, 600);
    gm.setTitle(formTitle);
    gm.setPreference("title", "Are you sure you want to remove the selected ports from the Local Circuit " + localCircuitName + "? \nThis action will remove remote site specific data such as remote circuit association, Network Gear, Serial Number, etc..");
    gm.setPreference("warning", "false");
    gm.setPreference("onPromptComplete", removePortsFromLocalCircuit);
    gm.setPreference("onPromptCancel", cancelRemovePorts);
    gm.render();

}

function cancelRemovePorts() {
    return false;
}

function removePortsFromLocalCircuit() {
    var localCircuitInformation = g_scratchpad.localCircuitInformation
    //var site = siteInformation.selectedSiteSysId;

    var cmdbCISite = new GlideAjax('x_g_sonc_ns_pro.port_ajax');
    cmdbCISite.addParam('sysparm_name', 'removePortsFromLocalCircuit');
    cmdbCISite.addParam('sysparm_checkedPorts', localCircuitInformation.checkedPorts);
    cmdbCISite.addParam('sysparm_rowSysID', localCircuitInformation.localCircuitID);
    cmdbCISite.getXMLAnswer(getResponse);

    function getResponse(response) {

		response = JSON.parse(response);
		//alert(response.resultCode);
        if (response.resultCode == "success") {
            location.reload();
        } else {
             return false;
        }
    }


}
