sys_ui_action.script:
action.setRedirectURL(current);
current.update();
if (!current.isActionAborted()) {
    // call a script include to perform the work
    // Instantiate the nsw2 utilities
    var nsw2util = new NcditNsw2Utils2();
    var cpeResp = nsw2util.reqCpeAllocationByFSFromClient(current);

	// gs.addInfoMessage(JSON.stringify(cpeResp));

    if (cpeResp.status == 'OK') {
        gs.addInfoMessage(cpeResp.message);
		current.provisioning_state='7';
		current.update();
    } else {
        gs.addErrorMessage("Server side function reqCpeAllocationByFSFromClient to allocate CPE was called and returned " + cpeResp.status + " , " + cpeResp.message);
    }

}
// redirect to here
action.setRedirectURL(current);
