sys_ui_action.script:
function onClick() {

	// alert('onClick running');

	// update form with current values to pending panel
	g_form.setValue('x_g_sonc_ns_pro_pending_location', g_form.getValue('location'));
	g_form.setValue('x_g_sonc_ns_pro_pending_status', g_form.getValue('install_status'));
	g_form.setValue('x_g_sonc_ns_pro_pending_start_date', g_form.getValue('start_date'));
	g_form.setValue('x_g_sonc_ns_pro_pending_floor_suite', g_form.getValue('x_g_sonc_ns_pro_floor_suite'));
	g_form.setValue('x_g_sonc_ns_pro_pending_decommission_date', g_form.getValue('x_g_sonc_ns_pro_decommission_date'));
	g_form.setValue('x_g_sonc_ns_pro_pending_last_upgrade', g_form.getValue('x_g_sonc_ns_pro_last_upgrade'));
	g_form.setValue('x_g_sonc_ns_pro_pending_name', g_form.getValue('name'));
	g_form.setValue('x_g_sonc_ns_pro_pending_primary_poc', g_form.getValue('x_g_sonc_ns_pro_primary_poc'));
	g_form.setValue('x_g_sonc_ns_pro_pending_alternate_poc', g_form.getValue('x_g_sonc_ns_pro_alternate_poc'));
	g_form.setValue('x_g_sonc_ns_pro_pending_office_hours', g_form.getValue('x_g_sonc_ns_pro_office_hours'));
	g_form.setValue('x_g_sonc_ns_pro_pending_telco_room', g_form.getValue('x_g_sonc_ns_pro_telco_room'));
	g_form.setValue('x_g_sonc_ns_pro_pending_demarc_location', g_form.getValue('x_g_sonc_ns_pro_demarc_location'));
	g_form.setValue('x_g_sonc_ns_pro_pending_agency', g_form.getValue('x_g_sonc_ns_pro_agency'));

	g_form.save();

}
