function openPortsList() {

    var selectedHostCircuit = g_form.getValue('x_g_sonc_ns_pro_pe_host_circuit_id');
    var selectedTelcoProvider = g_form.getValue('x_g_sonc_ns_pro_telco_provider', );
    var selectedHostCircuitName = g_form.getValue('x_g_sonc_ns_pro_pe_host_circuit_id');
    var selectedLocalCircuitName = g_form.getValue('name');
    var selectedLocalCircuitSysID = g_form.getUniqueValue();
    var localCircuitInfo = {};
    localCircuitInfo.selectedLocalCircuitName = selectedLocalCircuitName;
    localCircuitInfo.selectedTelcoProvider = selectedTelcoProvider;
    localCircuitInfo.selectedHostCircuitSysId = selectedHostCircuit;
    g_scratchpad.localCircuitInfo = localCircuitInfo;
    var parmFixedQuery = "siteISEMPTY^install_statusIN1,4^pe_host_circuit_id=" + selectedHostCircuit;

    var url;
    url = new GlideURL("x_g_sonc_ns_pro_port_list.do");
    url.addParam("sysparm_fixed_query", parmFixedQuery);
    url.addParam("sysparm_view", "selectavailableportscircuit");
    url.addParam("sysparm_view_forced", true);


    var portListW = new GlideOverlay({
        title: getMessage('Select Ports to Assign to Local Circuit ' + selectedLocalCircuitName),
        iframe: url.getURL(),
        draggable: true,
        allowOverflowX: true,
        scroll: true,
        height: "70%",
        width: "90%",
        closeOnEscape: true,
        //onAfterLoad: resizeIframe,
        onAfterClose: function() {
            location.reload();

        }
    });


//     var w = new GlideModal('show_list');
//    w.setTitle('Select Ports to Assign to Local Circuit ' + selectedLocalCircuitName);
//    w.setPreference('table', 'x_g_sonc_ns_pro_port_list');
//    w.setWidth(750);
//    w.setPreference('sysparm_sys_id', selectedHostCircuit);
//    w.setPreference('sysparm_view_forced', true);
//    w.setPreference('sysparm_view', 'selectavailableportscircuit');
//    // //w.setPreference('sysparm_force_row_count',20);
//    w.setPreference('sysparm_fixed_query', parmFixedQuery);
//    // //w.setPreference('onTableLoad',function(){g_list.setRowsPerPage(20);g_list.refresh();});
//    // //w.setPreference('onRefresh',function(){g_list.setRowsPerPage(20);});
//    //w.setPreference.setPreference('onPromptCancel', onPromptCancel);
//    w.setPreference('focusTrap', true);
	portListW.setPreference('sysparm_sys_id', selectedHostCircuit);
    portListW.render();
    g_list.refresh(1);
    //w.destroy();



}
