sys_ui_action.script:
function confirmUnAssignPortsFromSite() {
    var siteInformation = g_scratchpad.siteInformation;
    var selectedSiteID = siteInformation.selectedSiteID;
    var selectedSite = siteInformation.selectedSite;
    //alert(siteInformation.selectedSiteSysId);
    g_scratchpad.checkedPorts = g_list.getChecked();

    var gm = new GlideModal("glide_confirm_standard", true, 600);
    gm.setTitle("Remove Ports from Site " + selectedSiteID + " - " + selectedSite);
    gm.setPreference("title", "Are you sure you want to remove the selected ports from the site " + selectedSiteID + " - " + selectedSite + "? \nThis action will remove remote site specific data such as remote circuit association, Network Gear, Serial Number, etc..");
    gm.setPreference("warning", "false");
    gm.setPreference("onPromptComplete", removePortsFromSite);
    gm.setPreference("onPromptCancel", cancelRemovePorts);
    gm.render();

}

function cancelRemovePorts() {
    return false;
}

function removePortsFromSite() {
    var siteInformation = g_scratchpad.siteInformation;
    var checkedPorts = g_scratchpad.checkedPorts;
    var site = siteInformation.selectedSiteSysId;
    var cmdbCISite = new GlideAjax('x_g_sonc_ns_pro.cmdb_ci_site_ajax');
    cmdbCISite.addParam('sysparm_name', 'removePortsFromSite');
    cmdbCISite.addParam('sysparm_checkedPorts', checkedPorts);
    cmdbCISite.addParam('sysparm_site', site);
    cmdbCISite.getXMLAnswer(getResponse);

    function getResponse(response) {
		//alert(response);
        if (response == "success") {
            location.reload();
        } else {
             return false;
        }
    }


}
