sys_ui_context_menu.action_script:
/**
 * Script executed on the Client for this menu action
 *
 * The following variables are available to the script:
 *    'g_list' the GlideList2 that the script is running against (only valid for List context menus)
 *    'g_fieldName' the name of the field that the context menu is running against (only valid for List context menus)
 *    'g_fieldLabel' the label of the field that the context menu is running against (only valid for List context menus)
 *    'g_sysId' the sys_id of the row or form that the script is running against
 *    'rowSysId' is also set to the sys_id of the row to support legacy actions, but g_sysId is preferred
 */
var nsProTaskMain;

nsProTaskMain = parent.top.window.gsft_main;
assignHostPortToSite();

function assignHostPortToSite() {
    // var taskInformation;
	var site;
    // if (g_scratchpad.taskInformation) {
    //     taskInformation = g_scratchpad.taskInformation;
	// 	site = taskInformation.site;
    // }
	site = nsProTaskMain.g_form.getValue('site');

	//alert(site);
    var formTitle = "Assign Port";

    var gmf = new GlideModalForm(formTitle, 'x_g_sonc_ns_pro_port');
    gmf.addParm('sys_id', g_sysId);
    gmf.setOnloadCallback(populateDefaults);

    gmf.render();

    function populateDefaults(modalForm) {
        var iframe = modalForm.$window.find("#dialog_frame")[0];
        var iframeWindow = iframe.contentWindow ? iframe.contentWindow : iframe.contentDocument.defaultView;
        var d_form = iframeWindow.g_form;
        d_form.setValue('site', site);
//		jQuery(".modal-content").css("width", "95%").css("margin", "0 auto");
    }
    // var selectedHostCircuitName = g_form.getValue('name');
    // var selectedHostCircuitSysId = g_form.getUniqueValue();
    // var selectedHostPortSysId = g_sysId;
    // var selectHostPort = g_fieldName;

    //alert(selectHostPort);
    location.reload;
}

sys_ui_context_menu.on_show_script:
/**
 * This client-side script is used to dynamically change the context menu before it is displayed.
 * It is called before showing the context menu if the Run onShow script flag is checked.
 *
 * The following variables are available to the script:
 *    'g_menu' the context menu that is about to be shown
 *    'g_item' the current context menu item
 *    'g_list' the GlideList2 that the script is running against (only valid for List context menus)
 *    'g_fieldName' the name of the field that the context menu is running against (only valid for List context menus)
 *    'g_fieldLabel' the label of the field that the context menu is running against (only valid for List context menus)
 *    'g_sysId' the sys_id of the row or form that the script is running against
 */
canBeAssigned();

function canBeAssigned() {
    //alert(g_list.getView());
    var assignable = false;
    var selectedRecord = g_sysId;
    //var hostCircuitPort = new GlideRecord('x_g_sonc_ns_pro_port');
    // if (hostCircuitPort.get(selectedRecord)) {
    //     if ((hostCircuitPort.getValue('install_status') == "4" || hostCircuitPort.getValue('install_status') == "1" ) && hostCircuit.getValue('pe_host_circuit_id') != null) {
    //         if (g_list.getView() == 'selectavailableportshost' && g_list.getView() != 'selectavailableportscircuit') {
    //             assignable = true;
    //         } else {
    // 			assignable = false;
    // 		}

    //     }
    // }

    if (g_list.getView() == 'selectavailableportshost' && g_list.getView() != 'selectavailableportscircuit') {
        assignable = true;
    } else {
        assignable = false;
    }

    //alert(assignable);
    if (assignable) {
        g_menu.setEnabled(g_item);
		g_menu.setLabel(g_item, "<b>• Assign Host Port •</b>");
    } else {
        g_menu.setDisabled(g_item);
    }

}
