sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
    // called after user selects a service
    const funcName = "calculateQuote ";
    console.info(funcName, "has been called");

    //console.log(funcName, JSON.stringify(event,null,4)); // for debug

    const qMsg = api.state.zip2Search + ", " + event.payload.row.displayValue.value;
    api.setState('qqQuoteSubtitle', qMsg);

    // set lookup to trigger get specific clicked service def record.
    const svcCodeInfo = event.payload.row.rowData.value.get('svc_code');
    console.info(funcName, "setting svcCodeLookup1 to:", svcCodeInfo);
    api.setState('svcCodeLookup1', "svc_code=" + svcCodeInfo.columnData.value);

    return;
}
